import logo from '../images/logo.svg';
import header_img from '../images/header_img.jpg';
import footer from '../images/footerimage.jpg';
import uzh from '../images/uzh.svg';
import './App.css';
import { report } from './data.js';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';

class App extends React.Component {
    render() {
        return <div className="App">
            <header className="App-header" style= { {
                backgroundImage: `url(${header_img})`
            }}>

            </header>
            <body>
            <div className="light">
                <h2 className="section">{report[0].section}</h2>
                <div className="textbox">
                    <img className="text-image" src={uzh} alt={"uzh_logo"}/>
                    <p className="text">
                        {report[0].text}
                    </p>
                </div>
            </div>
            <div className="dark">
                <h2 className="section">{report[1].section}</h2>
                <div className="textbox">
                    <img className="text-image" src={uzh} alt={"uzh_logo"}/>
                    <p className="text">
                        {report[1].text}
                    </p>
                </div>
            </div>
            </body>
            <footer className="App-footer" style= { {
                backgroundImage: `url(${footer})`
            }}>
                <div className="header-footer-content-box" style={{position:"relative", left:"10%"}}>
                    <h3>AI Technology and Law</h3>
                    <hr className="solid"></hr>
                    <h6>Course: AI: Technology and Law (FS21)</h6>
                    <h6>Lecturers: Prof. Abraham Bernstein PhD & Prof. Dr. Florent Thouvenin</h6>
                    <h6>The research project was executed by Tjasa Marincek, Janik LÃ¼chinger, Damaris Schmid, CÃ©dric Zellweger & Adrian Zermin</h6>


                </div>

            </footer>
        </div>
    }
}

export default App;
