import footer from '../images/footerimage.jpg';
import uzh from '../images/uzh.svg';
import './App.css';
import { report } from './data.js';
import Header from "./header";
import Lawrgminer from './lawrgminer';
import Button from 'react-bootstrap/Button';
import React from 'react';

class App extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            prevScrollpos: window.pageYOffset,
            visible: true
        };
    }
    render() {
        return <div className="App">
            <Header />
            <body>
            <div className="light">
                <h2 id='introduction' className="section">{report[0].section}</h2>
                <div className="textbox">
                    <img className="text-image" src={uzh} alt={"uzh_logo"}/>
                    <p className="text">
                        {report[0].text}
                    </p>
                </div>
            </div>
            <div className="dark">
                <h2 className="section">{report[1].section}</h2>
                <div className="textbox">
                    <img className="text-image" src={uzh} alt={"uzh_logo"}/>
                    <p className="text">
                        {report[1].text}
                    </p>
                </div>
            </div>
            <Lawrgminer />
            </body>
            <footer className="App-footer" style= { {
                backgroundImage: `url(${footer})`
            }}>
                <div className="header-footer-content-box" style={{position:"relative", left:"10%"}}>
                    <h3>AI Technology and Law</h3>
                    <hr className="solid"></hr>
                    <h6>Course: AI: Technology and Law (FS21)</h6>
                    <h6>Lecturers: Prof. Abraham Bernstein PhD & Prof. Dr. Florent Thouvenin</h6>
                    <h6>The research project was executed by Tjasa Marincek, Janik Lüchinger, Damaris Schmid, Cédric Zellweger & Adrian Zermin</h6>


                </div>

            </footer>
        </div>
    }
}

export default App;
